mapper.select:
SELECT abc.did,abc.type,abc.pre,abc.imgurl,abc.sn,abc.parentid,abc.dictname, (CASE abc.type WHEN 1 THEN abc.DictName WHEN 2 THEN CONCAT(abc.brand,'-',abc.DictName) WHEN 3 THEN CONCAT(abc.sort,'-',abc.brand,'-',abc.DictName) END) AS bindinfo FROM ( SELECT a.*, ( SELECT aa.DictName FROM pro_dict aa WHERE aa.DID= a.ParentID) AS brand,
( SELECT cc.DictName FROM pro_dict cc WHERE cc.DID =( SELECT bb.ParentID FROM pro_dict bb WHERE bb.DID= a.ParentID)) AS sort FROM pro_dict a WHERE 1=1
AND a.`Type` =#{type, jdbcType=INTEGER}
AND a.`parentid` =#{parentId, jdbcType=INTEGER}
AND a.Pre =#{pre, jdbcType=VARCHAR}
AND a.DictName LIKE CONCAT(CONCAT('%',#{dictname,jdbcType=VARCHAR}),'%')
) abc order by abc.sn ASC ,abc.did DESC

SELECT abc.did,abc.type,abc.pre,abc.imgurl,abc.sn,abc.parentid,abc.dictname, (CASE abc.type WHEN 1 THEN abc.DictName WHEN 2 THEN CONCAT(abc.brand,'-',abc.DictName) WHEN 3 THEN CONCAT(abc.sort,'-',abc.brand,'-',abc.DictName) END) AS bindinfo FROM ( SELECT a.*, ( SELECT aa.DictName FROM pro_dict aa WHERE aa.DID= a.ParentID) AS brand,
( SELECT cc.DictName FROM pro_dict cc WHERE cc.DID =( SELECT bb.ParentID FROM pro_dict bb WHERE bb.DID= a.ParentID)) AS sort FROM pro_dict a WHERE 1=1
AND a.`Type` =#{type, jdbcType=INTEGER}
AND a.`parentid` =#{parentId, jdbcType=INTEGER}
AND a.Pre BETWEEN #{pre1, jdbcType=VARCHAR} and #{pre2, jdbcType=VARCHAR}
AND a.DictName LIKE CONCAT(CONCAT('%',#{dictname,jdbcType=VARCHAR}),'%')
) abc order by abc.sn ASC ,abc.did DESC

select a.DID,concat(a.Pre,'-',a.DictName) as DictName,a.ParentID from Pro_Dict a where a.`Type`=#{type,jdbcType=INTEGER}

  AND a.ParentID = #{parentid,jdbcType=INTEGER}

group by a.DictName,a.DID
